#! /usr/bin/env lua

require "lfs"
require "pl"
stringx.import()

outputName = arg[1]
headerName = outputName:upper():gsub("[^%w]", "_")

o = [[
/***** Generated by BuildGen S.c header generator *****/
#ifndef ]] .. headerName .. [[

#define ]] .. headerName .. [[


]]

local i = 1
while #arg > i do
	i = i+1

	local arg = arg[i]
	local key, value

	local b = arg:find("=") -- Find the end of the key.
	key, value = arg:sub(0, b-1), arg:sub(b+1) -- Split into the key and value

	if not key:match("^[%a_][%w_]*$") then
		error('S.c.generateHeader given an invalid key "'..key..'"')
	end

	value = value:gsub('.', {
	                          ["\\"] = "\\".."\\",
	                          ['"' ] = '\\"',
	                          ["\n"] = "\\n"
	                        })

	o = o.."const char *"..key..' = "'..value..'";\n'
end

o = o..[[

#endif /* ]] .. headerName .. [[ */
]]

file.write(outputName, o)
